["488:14"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Złap mnie za rękę!", ["ORYG"]="Take my hand!"},
["488:15"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Nie wyrwiemy się!", ["ORYG"]="We can’t break free!"},
["488:16"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Varianie!", ["ORYG"]="Varian!"},
["488:17"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Varianie!", ["ORYG"]="VARIAN!"},
["488:18"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Zabierz to", ["ORYG"]="Take this"},
["488:19"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="do mego syna.", ["ORYG"]="to my son."},
["488:20"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Ruchy, ruchy!", ["ORYG"]="Go go go!"},
["488:21"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Zapamiętają cię", ["ORYG"]="You will be remembered"},
["488:22"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="jako króla, który poświęcił swe życie", ["ORYG"]="as the king who sacrificed his life"},
["488:23"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="na próżno.", ["ORYG"]="for nothing."},
["488:24"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Na chwałę Przymierza.", ["ORYG"]="For the Alliance."},
["489:01"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Azeroth...", ["ORYG"]="Azeroth ..."},
["489:02"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Modlę się, by ten ostatni apel Armii Światłości", ["ORYG"]="I pray that this, the final plea of the Army of the Light"},
["489:03"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="bezpiecznie do was dotarł.", ["ORYG"]="has reached you intact."},
["489:04"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Tysiącletnia wojna osiągnęła swój szczyt,", ["ORYG"]="The thousand years' war has reached its peak"},
["489:05"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ale koszmar nie dobiegł jeszcze końca.", ["ORYG"]="but the nightmare is far from over."},
["489:06"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="To naczynie symbolizuje nasze ostateczne poświęcenie.", ["ORYG"]="This vessel represents our ultimate sacrifice."},
["489:07"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Musi trafić do proroka.", ["ORYG"]="It must reach the prophet."},
["489:08"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="To on... jest kluczem.", ["ORYG"]="He ... is the key."},
["489:09"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Żadne z nas nie prosiło o to brzemię,", ["ORYG"]="None of us asked for this burden"},
["489:10"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ale tak zadecydował los.", ["ORYG"]="but it is what fate has deemed."},
["489:11"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Jeśli się wam powiedzie, światłość zdoła jeszcze zwyciężyć.", ["ORYG"]="Succeed, and the light may yet endure."},
["489:12"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Zawiedźcie, a wszystkie światy...", ["ORYG"]="Fail, and all worlds ..."},
["489:13"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="spłoną.", ["ORYG"]="will burn."},
["489:14"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Nie zawiedziemy,", ["ORYG"]="We will not fail"},
["489:15"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Turalyonie.", ["ORYG"]="Turalyon."},
