["930:42"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Anduinie!", ["ORYG"]="Anduin!"},
["930:43"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Nie widzisz tego, stary przyjacielu?", ["ORYG"]="Don't you see, old friend?"},
["930:44"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Już się zaczęło.", ["ORYG"]="It's already begun."},
["930:45"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Dobrze więc, doradco.", ["ORYG"]="Very well, advisor."},
["930:46"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Powiedz nam, jak go zatrzymać.", ["ORYG"]="Tell us how to stop him."},
["932:01"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ŚWIST WIATRU", ["ORYG"]="WIND WHISTLING"},
["932:02"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="CHRAPLIWY ODDECH", ["ORYG"]="RASPY BREATHING"},
["932:03"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ZBLIŻAJĄCE SIĘ KROKI", ["ORYG"]="FOOTSTEPS APPROACHING"},
["932:04"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="METALICZNE TARCIE", ["ORYG"]="METALLIC SCRAPING"},
["932:05"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="CHRAPLIWY ODDECH", ["ORYG"]="RASPY BREATHING"},
["932:06"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="UTHER: Światłości... ocal mą duszę.", ["ORYG"]="UTHER: Light... save my soul."},
["932:07"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="# PODNIOSŁA MUZYKA ORKIESTROWA", ["ORYG"]="# GRAND ORCHESTRAL MUSIC"},
["932:08"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Utherze...", ["ORYG"]="DEVOS: Uther..."},
["932:09"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="UTHER: Gdzie... Gdzie ja jestem?", ["ORYG"]="UTHER: Where... Where am I?"},
["932:10"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Witaj... w Bastionie.", ["ORYG"]="DEVOS: Welcome... to Bastion."},
["932:11"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Całe swe życie poświęciłeś dla dobra innych", ["ORYG"]="You led a truly selfless life,"},
["932:12"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="i w nagrodę", ["ORYG"]="and for that,"},
["932:13"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="zostałeś wybrany.", ["ORYG"]="you have been chosen."},
["932:14"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="UTHER: Wybrany?", ["ORYG"]="UTHER: Chosen?"},
["932:15"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Masz odrzucić swe śmiertelne brzemię", ["ORYG"]="DEVOS: To shed your mortal burdens"},
["932:16"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="i wstąpić w szeregi wyniesionych,", ["ORYG"]="and join the ranks of the ascended,"},
["932:17"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="którzy przenoszą dusze zmarłych", ["ORYG"]="serving to ferry the souls of the dead"},
