["932:66"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Zdradził nas wszystkich.", ["ORYG"]="He betrayed us all."},
["932:67"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Pokaż.", ["ORYG"]="DEVOS: Show me."},
["932:68"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="KRZYK UTHERA", ["ORYG"]="UTHER SCREAMS"},
["932:69"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS ŁAPIE POWIETRZE", ["ORYG"]="DEVOS GASPS"},
["932:70"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ROZMOWY PATRONÓW", ["ORYG"]="PARAGONS CHATTERING"},
["932:71"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Run z jego ostrza", ["ORYG"]="DEVOS: The runes on his blade"},
["932:72"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="nie sposób pomylić!", ["ORYG"]="were unmistakable!"},
["932:73"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Sługa ciemności", ["ORYG"]="This dark agent runs free"},
["932:74"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="kroczy wśród śmiertelników,", ["ORYG"]="on a mortal world"},
