["932:76"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Nasz świat jest zagrożony!", ["ORYG"]="Our realm is imperiled!"},
["932:77"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="THENIOS: To niemożliwe!", ["ORYG"]="THENIOS: Impossible!"},
