["932:80"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Gdyby wyzbył się wspomnień,", ["ORYG"]="DEVOS: If he had purged his life,"},
["932:81"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="nigdy byśmy się nie dowiedzieli", ["ORYG"]="we never would have known"},
["932:82"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="o tym zagrożeniu!", ["ORYG"]="of this calamity!"},
["932:83"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Ścieżka jest niedoskonała!", ["ORYG"]="The path is flawed!"},
["932:84"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ARCHONTKA: Dość tego!", ["ORYG"]="ARCHON: Enough!"},
["932:85"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Ład Krainy Cieni zależy", ["ORYG"]="The order of the Shadowlands depends"},
["932:86"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="od wykonywania naszych odwiecznych obowiązków.", ["ORYG"]="on the execution of our eternal charge."},
["932:87"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Masz porzucić tę drogę.", ["ORYG"]="You will abandon this course."},
