["932:89"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Utherze!", ["ORYG"]="DEVOS: Uther!"},
["932:90"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Nadszedł czas twojego wyniesienia!", ["ORYG"]="The time of your ascension has come."},
["932:91"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="UTHER: Myślałem... że nie jestem gotów.", ["ORYG"]="UTHER: I... thought I was not ready."},
["932:92"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Czy chcesz, by poniósł karę?", ["ORYG"]="DEVOS: Do you wish to see him punished?"},
["932:93"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="UTHER: Tak, chcę.", ["ORYG"]="UTHER: I do."},
["932:94"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Zatem przygotuj się.", ["ORYG"]="DEVOS: Then prepare yourself."},
["932:95"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Gdy tylko zostanie pokonany...", ["ORYG"]="The moment he falls..."},
["932:96"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="wpadnie w nasze ręce.", ["ORYG"]="we will claim him."},
["932:97"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="WYCIE WIATRU", ["ORYG"]="WIND HOWLING"},
["932:98"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ARTHAS: Widzę...", ["ORYG"]="ARTHAS: I see..."},
["932:99"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="przed sobą...", ["ORYG"]="only darkness..."},
["932:100"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="tylko ciemność.", ["ORYG"]="before me."},
