["932:102"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Niech wraca w ciemność, której służył!", ["ORYG"]="Return him to the darkness he served!"},
["932:103"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Czeka go wieczna kaźń!", ["ORYG"]="His eternal suffering awaits."},
["932:104"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="UTHER: On był...", ["ORYG"]="UTHER: He was..."},
["932:105"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="moim uczniem.", ["ORYG"]="my student."},
["932:106"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DEVOS: Pamiętaj, co uczynił.", ["ORYG"]="DEVOS: Remember what he did"},
["932:107"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="Dopełnij zemsty.", ["ORYG"]="and take your vengeance."},
["932:108"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="UTHER: To nie zemsta.", ["ORYG"]="UTHER: Not vengeance."},
["932:109"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="To sprawiedliwość.", ["ORYG"]="Justice."},
