["933:01"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ORK KRZYCZY", ["ORYG"]="ORC SCREAMS"},
["933:02"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ORK DYSZY", ["ORYG"]="ORC PANTING"},
["933:03"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="ORK KRZYCZY", ["ORYG"]="ORC SCREAMS"},
["934:01"]={["START"]=0, ["STOP"]=0, ["NAPIS"]="DRAKA: Za życia zwałam się Draka", ["ORYG"]="DRAKA: In life, I was Draka,"},
