["Night Elf:06.29.24"]={["START"]=29.24, ["STOP"]=33.42, ["NAPIS"]="Gdy ze wszystkich stron nadciągają wojna i zniszczenie,", ["ORYG"]=""},
["Night Elf:07.33.42"]={["START"]=33.42, ["STOP"]=41.30, ["NAPIS"]="to Nocnym Elfom, takim jak Ty, przypada obowiązek zaparcia się\ni ochrony odwiecznego dziedzictwa swego ludu.", ["ORYG"]=""},
["Night Elf3:01.1.94"]={["START"]=1.94, ["STOP"]=4.08, ["NAPIS"]="Przez dziesięć tysięcy lat", ["ORYG"]=""},
["Night Elf3:02.4.08"]={["START"]=4.08, ["STOP"]=11.66, ["NAPIS"]="nieśmiertelne Nocne Elfy rozwijały druidyczne\nspołeczeństwo w zacienionych zaciszach Lasu Ashenvale.", ["ORYG"]=""},
["Night Elf3:03.11.66"]={["START"]=11.66, ["STOP"]=16.20, ["NAPIS"]="Lecz niedawno,\nkatastrofalna inwazja Płonącego Legionu", ["ORYG"]=""},
["Night Elf3:04.16.20"]={["START"]=16.20, ["STOP"]=20.50, ["NAPIS"]="zburzyła spokój ich starożytnej cywilizacji.", ["ORYG"]=""},
["Night Elf3:05.21.26"]={["START"]=21.26, ["STOP"]=26.98, ["NAPIS"]="Prowadzone przez Arcydruida Malfuriona Stormrage'a\ni Kapłankę Tyrande Whisperwind,", ["ORYG"]=""},
["Night Elf3:06.26.98"]={["START"]=26.98, ["STOP"]=31.62, ["NAPIS"]="potężne Nocne Elfy powstały,\nby zmierzyć się z demoniczną inwazją.", ["ORYG"]=""},
["Night Elf3:07.31.62"]={["START"]=31.62, ["STOP"]=37.78, ["NAPIS"]="Mimo zwycięstwa, Nocne Elfy były zmuszone\npoświęcić swą umiłowaną nieśmiertelność", ["ORYG"]=""},
["Night Elf3:08.37.78"]={["START"]=37.78, ["STOP"]=42.10, ["NAPIS"]="i patrzeć, jak ich ukochane lasy płoną.", ["ORYG"]=""},
["Night Elf3:09.42.12"]={["START"]=42.12, ["STOP"]=44.78, ["NAPIS"]="Pragnąc odzyskać swą nieśmiertelność,", ["ORYG"]=""},
["Night Elf3:10.44.78"]={["START"]=44.78, ["STOP"]=49.14, ["NAPIS"]="grupa krnąbrnych Druidów zmówiła się,\nby zasadzić wyjątkowe drzewo,", ["ORYG"]=""},
["Night Elf3:11.49.14"]={["START"]=49.14, ["STOP"]=53.06, ["NAPIS"]="które połączyłoby ich dusze\nze światem wieczności.", ["ORYG"]=""},
["Night Elf3:12.53.06"]={["START"]=53.06, ["STOP"]=58.44, ["NAPIS"]="Mimo ostrzeżeń Malfuriona, że Natura nigdy\nnie pobłogosławi tak samolubnego zachowania,", ["ORYG"]=""},
["Night Elf3:13.58.44"]={["START"]=58.44, ["STOP"]=65.12, ["NAPIS"]="Druidzi zasadzili wielkie drzewo Teldrassil\nu burzowych wybrzeży północnego Kalimdor.", ["ORYG"]=""},
["Night Elf3:14.65.12"]={["START"]=65.12, ["STOP"]=68.56, ["NAPIS"]="Między zmierzchowymi konarami olbrzymiego drzewa", ["ORYG"]=""},
["Night Elf3:15.68.56"]={["START"]=68.56, ["STOP"]=71.82, ["NAPIS"]="zakorzeniło się cudowne miasto Darnassus.", ["ORYG"]=""},
["Night Elf3:16.71.82"]={["START"]=71.82, ["STOP"]=76.32, ["NAPIS"]="Jednakże wielkie drzewo nie zostało\npoświęcone błogosławieństwem Natury", ["ORYG"]=""},
["Night Elf3:17.76.32"]={["START"]=76.32, ["STOP"]=80.66, ["NAPIS"]="i wkrótce stało się ofiarą splugawienia Płonącego Legionu.", ["ORYG"]=""},
["Night Elf3:18.80.66"]={["START"]=80.66, ["STOP"]=85.38, ["NAPIS"]="Teraz dzikie zwierzęta,\na nawet gałęzie samego wielkiego drzewa,", ["ORYG"]=""},
["Night Elf3:19.85.38"]={["START"]=85.38, ["STOP"]=89.22, ["NAPIS"]="są skażone wzbierającą ciemnością.", ["ORYG"]=""},
["Night Elf3:20.89.66"]={["START"]=89.66, ["STOP"]=93.02, ["NAPIS"]="Jako $g(jeden z ostatnich Nocnych Elfów;jedna z ostatnich Nocnych Elfek) na świecie", ["ORYG"]=""},
["Night Elf3:21.93.02"]={["START"]=93.02, ["STOP"]=99.04, ["NAPIS"]="masz obowiązek bronić Darnassus\ni dzikich dzieci natury", ["ORYG"]=""},
["Night Elf3:22.99.04"]={["START"]=99.04, ["STOP"]=103.84, ["NAPIS"]="przed wdzierającym się splugawieniem Legionu.", ["ORYG"]=""},
["Orc:01"]={["START"]=0.00, ["STOP"]=4.56, ["NAPIS"]="Wielki Kataklizm roztrzaskał więcej, niż tylko świat.", ["ORYG"]=""},
["Orc:02.4.56"]={["START"]=4.56, ["STOP"]=9.24, ["NAPIS"]="Wódz wojenny Thrall, prawdopodobnie najpotężniejszy z żyjących szamanów,", ["ORYG"]=""},
["Orc:03.9.24"]={["START"]=9.24, ["STOP"]=14.70, ["NAPIS"]="opuścił swoich ludzi, by powstrzymać Kataklizm u jego źródła.", ["ORYG"]=""},
["Orc:04.15.20"]={["START"]=15.20, ["STOP"]=19.98, ["NAPIS"]="Lecz podczas jego nieobecności, orkowy czempion, Garrosh Hellscream,", ["ORYG"]=""},
["Orc:05.19.98"]={["START"]=19.98, ["STOP"]=23.08, ["NAPIS"]="został nowym wodzem wojennym Hordy.", ["ORYG"]=""},
["Orc:06.23.08"]={["START"]=23.08, ["STOP"]=27.60, ["NAPIS"]="Mimo popularności pośród orków, jakie zapewniły mu\njego zwycięstwa w walce z Królem Liczem,", ["ORYG"]=""},
["Orc:07.27.60"]={["START"]=27.60, ["STOP"]=34.58, ["NAPIS"]="lekkomyślne przywództwo Garrosha zaczęło doprowadzać\ndo podziału między nim, a pozostałymi przywódcami Hordy.", ["ORYG"]=""},
["Orc:08.34.58"]={["START"]=34.58, ["STOP"]=37.80, ["NAPIS"]="Z niemal wyeksploatowanym z naturalnych zasobów Durotar,", ["ORYG"]=""},
["Orc:09.37.80"]={["START"]=37.80, ["STOP"]=41.60, ["NAPIS"]="Garrosh zamierza wziąć wszystko, czego jego ludzie potrzebują, by przetrwać,", ["ORYG"]=""},
["Orc:10.41.60"]={["START"]=41.60, ["STOP"]=45.28, ["NAPIS"]="bez względu na to, kto stanie mu na drodze.", ["ORYG"]=""},
["Orc:11.45.28"]={["START"]=45.28, ["STOP"]=48.58, ["NAPIS"]="Nastała dla Hordy nowa, niebezpieczna era,", ["ORYG"]=""},
["Orc:12.48.58"]={["START"]=48.58, ["STOP"]=51.56, ["NAPIS"]="i to na takich dzielnych orkach, jak Ty, spoczywa obowiązek", ["ORYG"]=""},
["Orc:13.51.56"]={["START"]=51.56, ["STOP"]=55.92, ["NAPIS"]="wcielenia w życie woli nowego wodza wojennego\ni zagwarantowania dominacji swego ludu.", ["ORYG"]=""},
["Orc3:01.4.22"]={["START"]=4.22, ["STOP"]=5.36, ["NAPIS"]="Dawno temu", ["ORYG"]=""},
["Orc3:02.5.36"]={["START"]=5.36, ["STOP"]=9.04, ["NAPIS"]="Orkowa Horda została\nsplugawiona przez Płonący Legion", ["ORYG"]=""},
["Orc3:03.9.04"]={["START"]=9.04, ["STOP"]=12.16, ["NAPIS"]="i zwabiona do świata Azeroth.", ["ORYG"]=""},
["Orc3:04.12.16"]={["START"]=12.16, ["STOP"]=19.46, ["NAPIS"]="Przez lata, Orkowie prowadzili wojnę\nw ludzkich królestwach Stormwind i Lordaeron.", ["ORYG"]=""},
["Orc3:05.19.90"]={["START"]=19.90, ["STOP"]=22.72, ["NAPIS"]="Mimo, że Horda została ostatecznie pokonana,", ["ORYG"]=""},
["Orc3:06.22.72"]={["START"]=22.72, ["STOP"]=25.82, ["NAPIS"]="młody, wizjonerski wódz wojenny imieniem Thrall", ["ORYG"]=""},
["Orc3:07.25.82"]={["START"]=25.82, ["STOP"]=29.64, ["NAPIS"]="powstał, by poprowadzić swój lud\nw jego najciemniejszej godzinie.", ["ORYG"]=""},
["Orc3:08.29.64"]={["START"]=29.64, ["STOP"]=35.46, ["NAPIS"]="Pod rządami Thralla, Orkowie uwolnili się\nz łańcuchów demonicznego splugawienia", ["ORYG"]=""},
["Orc3:09.35.46"]={["START"]=35.46, ["STOP"]=39.06, ["NAPIS"]="i przyjęli swe szamanistyczne dziedzictwo.", ["ORYG"]=""},
["Orc3:10.39.06"]={["START"]=39.06, ["STOP"]=41.38, ["NAPIS"]="Po latach wędrówek,", ["ORYG"]=""},
["Orc3:11.41.38"]={["START"]=41.38, ["STOP"]=47.12, ["NAPIS"]="Orkowie założyli własne królestwo\nna surowych pustkowiach Durotar.", ["ORYG"]=""},
["Orc3:12.47.12"]={["START"]=47.12, ["STOP"]=49.94, ["NAPIS"]="Z siedzibą w mieście wojowników, Orgrimmar,", ["ORYG"]=""},
["Orc3:13.49.94"]={["START"]=49.94, ["STOP"]=54.74, ["NAPIS"]="są gotowi zniszczyć każdego, kto podważy ich dominację.", ["ORYG"]=""},
["Orc3:14.54.74"]={["START"]=54.74, ["STOP"]=57.24, ["NAPIS"]="Jako $g(dumny obrońca;dumna obrończyni) Durotar,", ["ORYG"]=""},
["Orc3:15.57.24"]={["START"]=57.24, ["STOP"]=60.08, ["NAPIS"]="masz obowiązek zmiażdżyć swych wrogów,", ["ORYG"]=""},
["Orc3:16.60.08"]={["START"]=60.08, ["STOP"]=63.02, ["NAPIS"]="zarówno tych widocznych, jak i ukrytych,", ["ORYG"]=""},
["Orc3:17.63.02"]={["START"]=63.02, ["STOP"]=69.62, ["NAPIS"]="albowiem nikczemni wysłannicy\nPłonącego Legionu wciąż błąkają się po tej krainie.", ["ORYG"]=""},
["Pandaren:01"]={["START"]=0.00, ["STOP"]=7.34, ["NAPIS"]="Jesteś... pandarenem, urodzonym i wychowanym na tajemniczej, Wędrującej Wyspie.", ["ORYG"]=""},
["Pandaren:02.7.34"]={["START"]=7.34, ["STOP"]=12.20, ["NAPIS"]="Gdy wielu podobnych ci pandarenów\npreferuje spokojne życie,", ["ORYG"]=""},
["Pandaren:03.12.20"]={["START"]=12.20, ["STOP"]=16.34, ["NAPIS"]="w twoich żyłach wrze chęć przeżywania przygód.", ["ORYG"]=""},
["Pandaren:04.16.34"]={["START"]=16.34, ["STOP"]=24.10, ["NAPIS"]="Ostatnimi czasy Wędrująca Wyspa zaczęła\nnieregularnie krążyć po oceanach świata.", ["ORYG"]=""},
["Pandaren:05.24.10"]={["START"]=24.10, ["STOP"]=30.26, ["NAPIS"]="W powietrzu czuć chłód, a wyspa, którą nazywasz domem,\nzmierza ku katastrofie.", ["ORYG"]=""},
["Pandaren:06.30.26"]={["START"]=30.26, ["STOP"]=35.96, ["NAPIS"]="Będąc jednym z najbardziej obiecujących uczniów\nklasztoru Mistrza Shanga,", ["ORYG"]=""},
["Pandaren:07.35.96"]={["START"]=35.96, ["STOP"]=40.24, ["NAPIS"]="możesz okazać się wybawieniem dla swych ludzi.", ["ORYG"]=""},
["Pandaren:08.40.24"]={["START"]=40.24, ["STOP"]=44.76, ["NAPIS"]="Lecz wpierw... musisz ukończyć swój trening.", ["ORYG"]=""},
["Tauren:01"]={["START"]=0.00, ["STOP"]=3.22, ["NAPIS"]="Na plemiona Taurenów spadła wielka tragedia", ["ORYG"]=""},
["Tauren:02.3.22"]={["START"]=3.22, ["STOP"]=7.20, ["NAPIS"]="i wstrząsnęła ich niegdyś\nniezachwianym oddaniem wobec Hordy.", ["ORYG"]=""},
["Tauren:03.7.20"]={["START"]=7.20, ["STOP"]=11.44, ["NAPIS"]="Konflikt między nowym wodzem wojennym, orkiem Garroshem Hellscreamem,", ["ORYG"]=""},
["Tauren:04.11.44"]={["START"]=11.44, ["STOP"]=14.56, ["NAPIS"]="a ukochanym wodzem Taurenów, Cairnem Bloodhoofem,", ["ORYG"]=""},
["Tauren:05.14.56"]={["START"]=14.56, ["STOP"]=19.14, ["NAPIS"]="doprowadził do pojedynku, który zakończył się śmiercią Cairne'a.", ["ORYG"]=""},
["Tauren:06.19.14"]={["START"]=19.14, ["STOP"]=23.00, ["NAPIS"]="Po jego przegranej, nikczemna matriarchini, Magatha Grimtotem,", ["ORYG"]=""},
["Tauren:07.23.00"]={["START"]=23.00, ["STOP"]=28.60, ["NAPIS"]="przeprowadziła morderczy atak, próbując przejąć kontrolę nad Thunder Bluff.", ["ORYG"]=""},
["Tauren:08.29.02"]={["START"]=29.02, ["STOP"]=34.86, ["NAPIS"]="Jednakże dzielnemu synowi Cairne'a, Baine'owi Bloodhoofowi, udało się go odeprzeć,", ["ORYG"]=""},
["Tauren:09.34.86"]={["START"]=34.86, ["STOP"]=39.84, ["NAPIS"]="po czym wygnał ocalałych członków Ponurego Totemu na najdalsze krańce krainy.", ["ORYG"]=""},
["Tauren:10.40.62"]={["START"]=40.62, ["STOP"]=45.56, ["NAPIS"]="Pod budzącym nadzieję przywództwem Baine'a,\nTaureński lud dąży teraz do uleczenia swych ran", ["ORYG"]=""},
["Tauren:11.45.56"]={["START"]=45.56, ["STOP"]=49.94, ["NAPIS"]="i przywrócenia równowagi między różnymi frakcjami Hordy.", ["ORYG"]=""},
["Tauren:12.50.88"]={["START"]=50.88, ["STOP"]=55.00, ["NAPIS"]="W tym celu powstało nowe pokolenie taureńskich Słonecznych Wędrowców,", ["ORYG"]=""},
["Tauren:13.55.04"]={["START"]=55.04, ["STOP"]=60.96, ["NAPIS"]="które ma poprowadzić swój lud w świetle nowego świtu i obietnicy odrodzenia.", ["ORYG"]=""},
["Tauren:14.61.36"]={["START"]=61.36, ["STOP"]=66.00, ["NAPIS"]="Jako dumny członek plemienia Mulgore,\nmusisz podtrzymać honor swojego ludu", ["ORYG"]=""},
["Tauren:15.66.00"]={["START"]=66.00, ["STOP"]=69.60, ["NAPIS"]="i ochronić ziemie swych przodków.", ["ORYG"]=""},
["Tauren3:01.1.20"]={["START"]=1.20, ["STOP"]=3.16, ["NAPIS"]="Niegdyś koczowniczy lud,", ["ORYG"]=""},
["Tauren3:02.3.16"]={["START"]=3.16, ["STOP"]=8.84, ["NAPIS"]="Taureni błąkali się po nieskończonych\nrówninach Barrens, polując na potężne Kodo.", ["ORYG"]=""},
["Tauren3:03.9.12"]={["START"]=9.12, ["STOP"]=14.82, ["NAPIS"]="Rozsiane po całej krainie, wędrujące plemiona,\nzjednoczyła jedynie wspólna nienawiść", ["ORYG"]=""},
["Tauren3:04.14.82"]={["START"]=14.82, ["STOP"]=19.10, ["NAPIS"]="wobec ich zaprzysięgłego wroga, grabiących Centaurów.", ["ORYG"]=""},
["Tauren3:05.19.10"]={["START"]=19.10, ["STOP"]=23.94, ["NAPIS"]="Szukając pomocy w walce z Centaurami, wielki wódz Cairne Bloodhoof", ["ORYG"]=""},
["Tauren3:06.23.94"]={["START"]=23.94, ["STOP"]=28.94, ["NAPIS"]="zaprzyjaźnił się z okrutnymi orkami, którzy przybyli niedawno do Kalimdor.", ["ORYG"]=""},
["Tauren3:07.28.94"]={["START"]=28.94, ["STOP"]=33.76, ["NAPIS"]="Z pomocą orków, Cairne i jego plemię zdołali odeprzeć Centaury", ["ORYG"]=""},
["Tauren3:08.33.78"]={["START"]=33.78, ["STOP"]=37.68, ["NAPIS"]="i zdobyć łąki Mulgore na własność.", ["ORYG"]=""},
["Tauren3:09.38.00"]={["START"]=38.00, ["STOP"]=44.34, ["NAPIS"]="Na smaganym wiatrem urwisku Thunderbluff\nCairne zbudował schronienie dla swych ludzi.", ["ORYG"]=""},
["Tauren3:10.44.34"]={["START"]=44.34, ["STOP"]=49.66, ["NAPIS"]="Z czasem, rozproszone plemiona zjednoczyły się pod jednym sztandarem.", ["ORYG"]=""},
["Tauren3:11.50.44"]={["START"]=50.44, ["STOP"]=53.68, ["NAPIS"]="Mimo, że szlachetni Taureni są z natury pokojowi,", ["ORYG"]=""},
["Tauren3:12.53.68"]={["START"]=53.68, ["STOP"]=59.68, ["NAPIS"]="prawa Wielkich Łowów są czczone jako serce ich duchowej kultury.", ["ORYG"]=""},
["Tauren3:13.59.70"]={["START"]=59.70, ["STOP"]=62.10, ["NAPIS"]="Jako członek plemienia Mulgore", ["ORYG"]=""},
["Tauren3:14.62.10"]={["START"]=62.10, ["STOP"]=69.14, ["NAPIS"]="musisz przetestować swoje umiejętności w dziczy\ni wykazać się podczas Wielkich Łowów.", ["ORYG"]=""},
["Troll:01"]={["START"]=0.00, ["STOP"]=4.06, ["NAPIS"]="Wygnane lata temu ze swych ziemi w Stranglethorn Vale,", ["ORYG"]=""},
["Troll:02.4.06"]={["START"]=4.06, ["STOP"]=10.98, ["NAPIS"]="trolle Mrocznej Włóczni usiłowały\nrozpocząć nowe życie pośród ras Hordy.", ["ORYG"]=""},
["Troll:03.11.54"]={["START"]=11.54, ["STOP"]=14.54, ["NAPIS"]="Pod dowództwem szlachetnego wodza wojennego, Thralla,", ["ORYG"]=""},
["Troll:04.14.54"]={["START"]=14.54, ["STOP"]=20.98, ["NAPIS"]="Vol'Jin i jego barbarzyńskie trolle nareszcie\nodnaleźli honor i cel, którego szukali,", ["ORYG"]=""},
["Troll:05.21.56"]={["START"]=21.56, ["STOP"]=25.62, ["NAPIS"]="lecz teraz, pod rządami nieustępliwego Garrosha Hellscreama,", ["ORYG"]=""},
["Troll:06.25.66"]={["START"]=25.66, ["STOP"]=29.58, ["NAPIS"]="trolle obawiają się, że Horda może się rozpaść.", ["ORYG"]=""},
["Troll:07.30.20"]={["START"]=30.20, ["STOP"]=36.68, ["NAPIS"]="Vol'Jin wie, że bez względu na to, jakie pojawi się zagrożenie,\nczy to w Hordzie czy poza nią,", ["ORYG"]=""},
["Troll:08.36.70"]={["START"]=36.70, ["STOP"]=42.38, ["NAPIS"]="to przebiegłe trolle, takie jak Ty,\nbędą walczyć, by zachować honor Hordy.", ["ORYG"]=""},
["Troll3:01.2.36"]={["START"]=2.36, ["STOP"]=7.94, ["NAPIS"]="Agresywne trolle, które zamieszkują\nliczne dżungle wysp Mórz Południowych,", ["ORYG"]=""},
["Troll3:02.7.94"]={["START"]=7.94, ["STOP"]=11.80, ["NAPIS"]="są znane ze swojego\nokrucieństwa i mrocznego mistycyzmu.", ["ORYG"]=""},
["Troll3:03.12.10"]={["START"]=12.10, ["STOP"]=14.34, ["NAPIS"]="Barbarzyńskie i przesądne,", ["ORYG"]=""},
["Troll3:04.14.34"]={["START"]=14.34, ["STOP"]=18.74, ["NAPIS"]="darzą wrzącą nienawiścią pozostałe rasy.", ["ORYG"]=""},
["Troll3:05.19.44"]={["START"]=19.44, ["STOP"]=24.50, ["NAPIS"]="Wygnane dawno temu ze swej\nstarożytnej ojczyzny w Stranglethorn Vale,", ["ORYG"]=""},
["Troll3:06.24.54"]={["START"]=24.54, ["STOP"]=29.82, ["NAPIS"]="plemię Mrocznych Włóczni zostało niemal zniszczone przez szalejące murloki.", ["ORYG"]=""},
["Troll3:07.29.82"]={["START"]=29.82, ["STOP"]=34.54, ["NAPIS"]="Ocalone przez młodego Wodza Wojennego, Thralla, i jego orkowych wojowników,", ["ORYG"]=""},
["Troll3:08.34.56"]={["START"]=34.56, ["STOP"]=38.34, ["NAPIS"]="Plemię Mrocznej Włóczni\nprzysięgło wierność Hordzie.", ["ORYG"]=""},
["Troll3:09.38.34"]={["START"]=38.34, ["STOP"]=42.54, ["NAPIS"]="Prowadzone przez\nprzebiegłego Łowcę Cienia, Vol'jina,", ["ORYG"]=""},
["Troll3:10.42.54"]={["START"]=42.54, ["STOP"]=48.28, ["NAPIS"]="Mroczne Włócznie osiedlają się teraz w Durotar\nwraz ze swymi orkowymi sojusznikami.", ["ORYG"]=""},
["Troll3:11.48.28"]={["START"]=48.28, ["STOP"]=51.25, ["NAPIS"]="Będąc jednym z ocalałych plemienia Mrocznych Włóczni,", ["ORYG"]=""},
["Troll3:12.51.26"]={["START"]=51.26, ["STOP"]=56.60, ["NAPIS"]="Tobie przypada obowiązek\nodzyskania chwały swojego plemienia.", ["ORYG"]=""},
["Undead:01"]={["START"]=0.00, ["STOP"]=3.34, ["NAPIS"]="Mimo, że Lady Sylvanas i Zapomniani", ["ORYG"]=""},
["Undead:02.3.34"]={["START"]=3.34, ["STOP"]=7.36, ["NAPIS"]="nareszcie wywarli zemstę na swym\nznienawidzonym wrogu, Królu Liszu,", ["ORYG"]=""},
